import React, { useRef, useState, useEffect } from "react";
import {
    MDBContainer,
    MDBRow,
} from "mdbreact";
import Charts from "react-apexcharts";
export const CategoryChart = (props: any) => {
    const [chartoptions, setChartoptions] = useState({
        series: [14, 23, 21],
        options: {
            stroke: {
                colors: ['#fff']
            },
            colors: ['#7ed957', '#008037', '#2bbbad'],
            labels: ["Critical", "High", "Low"],

            dataLabels: {
                enabled: false,
            },
            yaxis: {
                show: false
            },
            fill: {
                opacity: 0.95
            },
            plotOptions: {
                polarArea: {
                  rings: {
                    strokeWidth: 0
                  },
                  spokes: {
                    strokeWidth: 0
                  },
                }
              },
              legend: {
                position: "right",
                offsetY: 40
            },
            responsive: [{
                breakpoint: 480,
                options: {
                    chart: {
                        width: 200
                    },
                    legend: {
                        position: 'bottom'
                    }
                }
            }]
        },


    });
    return (
        <>
            <Charts
                options={chartoptions.options}
                series={chartoptions.series}
                type="polarArea"
                width="280"
            />
        </>
    )
}
