                        let sortable: any = [];
                        for (const [key, value] of Object.entries(props.Kpi.metrics.vapt.categories)) {
                            sortable.push([key, value]);
                        }
                        sortable.sort((a: any, b: any) => {
                            return b[1] - a[1];
