                        pentestSeriesCategoryCount = [(sortable[0] as any)[1], (sortable[1] as any)[1], (sortable[2] as any)[1]];
                        setpentestSeriesCategoryCount(pentestSeriesCategoryCount);
                    }
                }
            }
        })
    }, [props]);

