import React, { useRef, useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBProgress,
    MDBListGroup,
    MDBListGroupItem
} from "mdbreact";
import { useHistory } from "react-router";
import { Chart } from "react-google-charts";
import "./style.css"
import ApexCharts from 'apexcharts';
import Charts from "react-apexcharts";
export const Home = (props: any) => {
    const token = localStorage.getItem("token");
    const [chartoptions, setChartoptions] = useState({
        options: {
            chart: {
                id: "bar",
            },
            xaxis: {
                categories: ["Critical", "High", "Low"],
                offsetY: -20,
                labels: {
                    show: false
                  },
                  axisBorder: {
                    show: true,
                    color: '#78909C',
                    height: 1,
                    width: '100%',
                    offsetX: 0,
                    offsetY: 0
                },
                axisTicks: {
                    show: true,
                    borderType: 'solid',
                    color: '#78909C',
                    height: 6,
                    offsetX: 0,
                    offsetY: 0
                },
            },

            colors: ['#f44336', '#ff9800', '#2bbbad', '#13d8aa', '#A5978B', '#2b908f', '#f9a3a4', '#90ee7e',
                '#f48024', '#69d2e7'
            ],
            dataLabels: {
                enabled: false,

            },
            legend: {
                position: "right",
                offsetY: 40
            },
            plotOptions: {
                bar: {
                    distributed: true,
                }
            },
            yaxis: {
                labels: {
                    show: false
                },
            },
            grid: {
               show: false,
                // padding: {
                //     top: 20,
                //     right: 0,
                //     bottom: 0,
                //     left: 0
                // },  
            }
        },
        series:
            [{
                name: "Vulnearbility",
                data: [30, 40, 45],
            }]

    });
    console.log(props)
    if (token === null || token === undefined || token.length < 0) {
        return <Redirect to={{
            pathname: '/login',
        }} />
    }
    return (
        <>
            <MDBContainer fluid className="pt-5 pb-5" style={{ padding: "2vw" }}>
                <MDBRow style={{height: "200px"}}>
                    <Charts
                        options={chartoptions.options}
                        series={chartoptions.series}
                        type="bar"
                        width="300"
                    />

                </MDBRow>
            </MDBContainer>
        </>
    )
}
