import React, { useRef, useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBProgress,
    MDBListGroup,
    MDBListGroupItem,
    MDBBreadcrumbItem,
    MDBBreadcrumb,
    MDBBadge,
    MDBCollapse
} from "mdbreact";
import { Chart } from "../common/VulnChart"
import { useHistory } from "react-router";
import "./style.css";
import { fetchData } from '../../services/apiConfig'
export const Pentest = (props: any) => {
    const [domains, setDomain] = useState([{
        "name": "Phase",
        "period": "quarter-3",
        "infoCount": 10,
        "lowCount": 10,
        "mediumCount": 10,
        "highCount": 10,
        "criticalCount": 10,
        "totalCount": 10,
        "lastSynced": "()"
    },
    {
        "name": "Phase",
        "period": "quarter-3",
        "infoCount": 10,
        "lowCount": 10,
        "mediumCount": 10,
        "highCount": 10,
        "criticalCount": 10,
        "totalCount": 10,
        "lastSynced": "September 06 2021, 02:15:08"
    }]);
    const [subDomains, setSubDomains] = useState(["subDomains"]);
    const [collapseID, setCollapseID] = useState("");
    const [selectedPhase, setSelectedPhase] = useState("")

    console.log(props)


    useEffect(() => {
        console.log(props.Account)
        if (props.Account.indexOf("quarter") > -1) {
            fetchData('vapt', 'GET', "?quarter=" + props.Account).then((res: any) => {
                console.log(res)
                setDomain(res)

            })
        }
        else {
            fetchData('vapt', 'GET', "?quarter=quarter-1").then((res: any) => {
                console.log(res)
                if (res.length > 0) {
                    setDomain(res)
                }
            })
        }
    }, [props.Account]);

    useEffect(() => {
        console.log(props.Account)

