                            <span className='text-dark' style={{ fontSize: "12px", marginLeft: "10px", paddingTop: "12px" }}>Last tested on 
                            {(domains.length > 0) ? domains[0].lastSynced : ""}</span>
                        </MDBRow>
                    </MDBCol>
                </MDBRow>
                <MDBRow >
                    <MDBCol sm="11" md="11" lg="11" >
                        <div style={{ maxHeight: "150vh", overflowY: "hidden", overflowX: "hidden" }}>
                            <MDBRow className="awsRow">

                                {(domains.length > 0) ?
                                    domains.map((dom: any) =>
                                        <>
                                            <MDBCol className="pentestList" sm="3" md="3" lg="3" ref="div"onClick={() => SubDomainSwitch( dom.name)}>
