                                        <MDBCol sm="6" md="6" lg="6">  <CategoryChart
                                            color={['#0f3c4c', '#1e7898', '#2596be', '#7cc0d8']}
                                            // color={['#CFAFAF', '#A96E6E', '#6F4242']} 
                                            series={[5, 3, 3]} /></MDBCol>
                                    </MDBRow>
                                    <br />
                                </div>
                            </>
                            <br /><br />
                        </MDBCol>
                    </MDBRow> 
                    :
                    <MDBRow style={{ minHeight: "80vh" }}>
                        <MDBCol sm="12" md="12" lg="6">
