import React, { useRef, useState, useEffect } from "react";
import {
    MDBContainer,
    MDBRow,
} from "mdbreact";
import Charts from "react-apexcharts";
export const Chart = (props: any) => {
    const [chartoptions, setChartoptions] = useState({
        options: {
            chart: {
                id: "bar",
            },
            xaxis: {
                categories: ["Critical", "High", "Low"],
                offsetY: 5,
                labels: {
                    show: false
                  },
                  axisBorder: {
                    show: true,
                    color: '#607d8b',
                    height: 4,
                    width: '100%',
                },
                axisTicks: {
                    show: false
                },
            },
            colors: ['#f42415', '#ff9800', '#2bbbad'],
            dataLabels: {
                enabled: false,

            },
            legend: {
                position: "right",
                offsetY: 40
            },
            plotOptions: {
                bar: {
                    distributed: true,
                }
            },
            yaxis: {
                labels: {
                    show: false
                },
            },
            grid: {
               show: false,
            }
        },
        series:
            [{
                name: "Vulnearbility",
                data: [30, 40, 45],
            }]

    });
    return (
        <>
            <MDBContainer fluid className="pt-5 pb-5" style={{ padding: "2vw" }}>
                <MDBRow>
                    <Charts
                        options={chartoptions.options}
                        series={chartoptions.series}
                        type="bar"
                        width="250"
                    />

                </MDBRow>
            </MDBContainer>
        </>
    )
}
