import React, { useRef, useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import { Home } from "./Home";
import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead
} from "mdbreact";
import { useHistory } from "react-router";
import logo from '../../assets/images/logo-white-large.png';
import "./style.css"

export const Dashboard = (props: any) => {
    const [view, setView] = useState('Home');
    console.log(props)
    const history = useHistory();
    if (props.User === undefined) {
        return <Redirect to={{
            pathname: '/login',
        }} />
    }
    if (props.User !== undefined && props.User.isOnboarded === false) {
        return <Redirect to={{
            pathname: '/onboarding',
        }} />
    }
    return (
        <>
            <MDBContainer fluid>
                <MDBRow>
                    <MDBCol sm='1' md='2'>
                        <MDBRow style={{ backgroundColor: "#29283a", height: "7vh", padding: "1vw" }}>
                            <div>
                                <img
                                    src={logo}
                                    className="img"
                                    alt=""
                                    style={{ height: "3vw" }}
                                />
                            </div>
                        </MDBRow>
                        <MDBRow style={{ backgroundColor: "#3c3a4f", height: "93vh", justifyContent: "center" }}>
                            <div style={{ marginTop: "25px" }}>
                                <p>
                                    <MDBIcon fab onClick={() => window.open('https://www.facebook.com/apnisec.in')} icon="facebook-f" style={{ fontSize: "20px", color: "#fbfbfd", cursor: "pointer" }} />
                                    <MDBIcon fab onClick={() => window.open('https://twitter.com/apnisec')} icon="twitter" style={{ fontSize: "20px", color: "#fbfbfd", cursor: "pointer" }} />
                                    <MDBIcon fab onClick={() => window.open('https://www.linkedin.com/company/apnisec')} icon="linkedin-in" style={{ fontSize: "20px", color: "#fbfbfd", cursor: "pointer" }} />
                                </p>
                            </div>
                            {/* Side navigation list  */}



                        </MDBRow>
                    </MDBCol>
                    <MDBCol sm='11' md='10'>

                        <Header />
                        {/* <MDBRow className="watcherHeader" style={{ backgroundColor: "white", height: "7vh", minHeight: "70px" }}>
                            <MDBCol sm="3" md="4" lg="4">
                                <div className='font-weight-bold' style={{ padding: "1.5vw", display: "inline-flex" }}>
                                    <p className='text-dark' style={{ fontSize: "20px" }}>Dashboard 
                                    <span className='text-light' style={{ fontSize: "12px", marginLeft: "10px" }}>www.sampleapplication.com</span></p>
                                </div>
                            </MDBCol>
                            <MDBCol sm="7" md="7" lg="7">
                                <div style={{ padding: "10px", float: "right" }}>
                                    <MDBBtn style={{ border: "2px solid #2bbbad", borderRadius: "10px" }}>Export 
                                    <MDBIcon icon="file-download" style={{ fontSize: "15px", marginLeft: "3px" }} /></MDBBtn>
                                    <MDBBtn style={{ border: "2px solid #2bbbad", borderRadius: "10px" }}>Notifications 
                                    <MDBIcon icon="bell" style={{ fontSize: "15px", marginLeft: "3px" }} /><span className="badge">3</span></MDBBtn>
                                </div>
                            </MDBCol>
                            <MDBCol sm="1" md="1" lg="1">
                                <div style={{ paddingTop: "20px" }}>
                                    <MDBIcon icon="user-circle" style={{ fontSize: "25px", marginLeft: "20px" }} />
                                </div>
                            </MDBCol>
                        </MDBRow> */}


                        <MDBRow style={{ backgroundColor: "#fbfbfd", height: "93vh" }}>
                            <MDBContainer>
                            {(() => {

                                switch (view) {
                                    case 'Home':
                                        return (
                                        <Home User={props.User}/>
                                        )
                                    case 'Manager':
                                        return (
                                        <div>You are a Manager.</div>
                                        )
                                    default:
                                        return (
                                        <div>You are a User.</div>
                                        )
                                }
                                })()}
                            </MDBContainer>
                        </MDBRow>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        </>
    )
}
