import React, { useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBProgress,
    MDBListGroup,
    MDBListGroupItem,
    MDBBreadcrumb,
    MDBBreadcrumbItem
} from "mdbreact";
import { useHistory } from "react-router";
import { Chart } from "react-google-charts";
import "./style.css";
import domain from '../../assets/images/domain.png'
import { fetchData } from '../../services/apiConfig'
export const PhaseSelector = (props: any) => {
    console.log(props)
    const [AccountList, setAccountList] = useState(props.Kpi.metrics.vapt.defaultQuarter);
    const [YearList, setYearList] = useState(props.Kpi.metrics.vapt.defaultYear);
    useEffect(() => {
            props.AccountToggle([YearList, AccountList]);
    }, [props]);

    const AccountT = (e: any) => {
        const newAccount = e.target.value;
        setAccountList(newAccount)
        props.AccountToggle(newAccount);
    }
    const Year = (e: any) => {
        const newAccount = e.target.value;
        setYearList(newAccount)
        props.AccountToggle(newAccount);
    }
    const token = localStorage.getItem("token");
    if (token === null || token === undefined || token.length < 0) {
        return <Redirect to={{
            pathname: '/login',
        }} />
    }
    return (
        <>
            <MDBContainer fluid style={{ paddingBottom: "2vw"}}>
                <MDBRow>
                <div>
                        <select className="custom-select" style={{ width: "250px" }} onChange={(e: any) => AccountT(e)} value={AccountList}>
                                 <option value="2021">2021</option>
                                 <option value="2022">2022</option>
                        </select>
                    </div>

                    <div style={{marginLeft: '5px'}}>
                        <select className="custom-select" style={{ width: "250px" }} onChange={(e: any) => AccountT(e)} value={AccountList}>
                                 <option value="Quarter-1">Quarter-1</option>
                                 <option value="Quarter-2">Quarter-2</option>
                                 <option value="Quarter-3">Quarter-3</option>
                                 <option value="Quarter-4">Quarter-4</option>
                        </select>
                    </div>
                </MDBRow>
            </MDBContainer>
        </>
    )
}
