import React, { useRef, useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import { Home } from "./Home";

import {
    MDBBtn,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBLink
} from "mdbreact";
import { useHistory } from "react-router";
import logo from '../../assets/images/logo-white-large.png';
import "./style.css"

export const Dashboard = (props: any) => {
    const [view, setView] = useState('Home');
    console.log(props)
    const history = useHistory();
    const Switch = (e:any) => {
        console.log(e)

    }
    if (props.User === undefined) {
        return <Redirect to={{
            pathname: '/login',
        }} />
    }
    if (props.User !== undefined && props.User.isOnboarded === false) {
        return <Redirect to={{
            pathname: '/onboarding',
        }} />
    }
    return (
        <>
            <MDBContainer fluid>
                <MDBRow>
                    <MDBCol xs="1" sm='1' md='2'>
                        <MDBRow style={{ backgroundColor: "#29283a", height: "7vh" }}>
                            <div>
                                <img
                                    src={logo}
                                    className="img"
                                    alt=""
                                    style={{ height: "4vw" }}
                                />
                            </div>
                        </MDBRow>
                        <MDBRow style={{ backgroundColor: "#3c3a4f", height: "93vh", justifyContent: "center" }}>
                            <div style={{ marginTop: "35px" }}>
                                <p>
                                    <MDBIcon fab onClick={() => window.open('https://www.facebook.com/apnisec.in')} icon="facebook-f" style={{ fontSize: "20px", color: "#fbfbfd", cursor: "pointer" }} />
                                    <MDBIcon fab onClick={() => window.open('https://twitter.com/apnisec')} icon="twitter" style={{ fontSize: "20px", color: "#fbfbfd", cursor: "pointer" }} />
                                    <MDBIcon fab onClick={() => window.open('https://www.linkedin.com/company/apnisec')} icon="linkedin-in" style={{ fontSize: "20px", color: "#fbfbfd", cursor: "pointer" }} />
                                </p>
                                <div style={{ marginTop: "35px", marginLeft: "10px"}}>
                                <MDBLink class="side-nav-links" to='#' onClick={() => Switch('home')} link>
                                     <MDBIcon icon="chart-line"/> &nbsp;&nbsp;Dashboard
                                </MDBLink>
                                <br />
                                <MDBLink class="side-nav-links" to='#' onClick={() => Switch('home')} link>
                                       <MDBIcon icon="database" /> &nbsp;&nbsp;Assets
                                </MDBLink>
                                <br />
                                <MDBLink class="side-nav-links" to='#' onClick={() => Switch('home')} link>
                                <MDBIcon icon="exclamation-triangle" /> &nbsp;&nbsp;Alerts
                                </MDBLink>
                                <br />
                                <MDBLink class="side-nav-links" to='#' onClick={() => Switch('home')} link>
                                <MDBIcon icon="wrench" /> &nbsp;&nbsp;Settings
                                </MDBLink>
                                <br />
                                <div style={{ marginTop: "40vh"}} >
                                <MDBLink class="side-nav-links-bottom" onClick={() => Switch('home')} link>
                                 9 days left of free trial
                                </MDBLink>
                                <br />
                                <MDBLink class="side-nav-links-bottom" to='#' onClick={() => Switch('home')} link>
                                <MDBIcon icon="envelope" /> &nbsp;&nbsp;Contact us
                                </MDBLink>
                                </div>
                                 </div>
                            </div>
                        </MDBRow>
                    </MDBCol>
                    <MDBCol xs="11" sm='11' md='10'>
                        <Header />
                        <MDBRow style={{ backgroundColor: "#fbfbfd", height: "93vh" }}>
                            <MDBContainer fluid>
                            {(() => {

                                switch (view) {
                                    case 'Home':
                                        return (
                                        <Home User={props.User}/>
                                        )
                                    case 'Manager':
                                        return (
                                        <div>You are a Manager.</div>
                                        )
                                    default:
                                        return (
                                        <div>You are a User.</div>
                                        )
                                }
                                })()}
                            </MDBContainer>
                        </MDBRow>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        </>
    )
}
