import React, { useRef, useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBProgress,
    MDBListGroup,
    MDBListGroupItem,
    MDBBreadcrumbItem,
    MDBBreadcrumb,
    MDBCollapse,
    MDBNotification,
    MDBBadge
} from "mdbreact";
import "./style.css";
import { fetchData } from '../../services/apiConfig'

export const Subdomains = (props: any) => {
    const [subDomainfullList, setsubDomainfullList] = useState(["subDomains"]);
    const [subDomains, setSubDomains] = useState(["subDomains"]);
    const [collapseID, setCollapseID] = useState("");
    const [vulnerability, setVulnerability] = useState('all');
    console.log(props)
    useEffect(() => {
        fetchData('subdomain', 'GET',  "?accountName=" + props.Account  + "&domain=" + props.Domain).then((res: any) => {
