import { authToken } from './auth'

export const headers = (method: any, body?: any) => {
    let headers: any;
    body ? 
    headers = {
        'method': method,
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + authToken(),
        'body': body
    } : 
    headers = {
        'method': method,
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + authToken(),
        'body': body
    } 
    console.log(headers);
    return headers
}

export const fetchData = (endpoint: any, method: any, body?: any) => {
    fetch('https://api.thewatcher.live//' + endpoint, body? headers(method, body) : headers(method) ).then(res => res.json()).then((data) => {
        console.log(data)
        return data
    })
}
