import { authToken } from './auth'

export const headers = (method: any, body: any) => {
    return {
        'method': method,
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + authToken(),
        'body': body
    }
}

export const fetchData = (endpoint: any, method: any, body: any) => {
    fetch('https://api.thewatcher.live//' + endpoint, headers(method, body)).then(res => res.json()).then((data) => {
        console.log(data)
        return data
    })
}
