import { authToken } from './auth'

export const getHeaders = () => {
    let headers: any;
    headers = {
        // 'Accept' : 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + authToken(),
    } 
    console.log(headers);
    return headers
}

export const fetchData = (endpoint: any, method: any, body?: any) => {
    const config = {
        'method': method,
        'headers': getHeaders(),
        'body': body,
    }
    console.log(config)
    fetch('https://api.thewatcher.live//' + endpoint,  config ).then(res => res.json()).then((data) => {
        console.log(data)
        return data
    })
}
