import React, { useRef, useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBProgress,
    MDBListGroup,
    MDBListGroupItem,
    MDBBreadcrumbItem,
    MDBBreadcrumb
} from "mdbreact";
import { Chart } from "../common/VulnChart"
import { useHistory } from "react-router";
import "./style.css";
import { fetchData } from '../../services/apiConfig'
export const Pentest = (props: any) => {
    const [domains, setDomain] = useState([{
        "name": "Phase",
        "period": "quarter-3",
        "infoCount": 10,
        "lowCount": 10,
        "mediumCount": 10,
        "highCount": 10,
        "criticalCount": 10,
        "totalCount": 10,
        "lastSynced": "()"
      },
      {
        "name": "Phase",
        "period": "quarter-3",
        "infoCount": 10,
        "lowCount": 10,
        "mediumCount": 10,
        "highCount": 10,
        "criticalCount": 10,
        "totalCount": 10,
        "lastSynced": "September 06 2021, 02:15:08"
      }]);

    console.log(props)


    useEffect(() => {
        console.log(props.Account)
       if (props.Account.indexOf("quarter") > -1) {
            fetchData('vapt', 'GET', "?quarter=" + props.Account).then((res: any) => {
                console.log(res)
                setDomain(res)

            })
       }
       else{
        fetchData('vapt', 'GET', "?quarter=quarter-1").then((res: any) => {
            console.log(res)
            if(res.length>0){
                setDomain(res)
            }
        })
   }
    }, [props.Account]);

    useEffect(() => {
        console.log(props.Account)

    }, [domains]);

    const SubDomainSwitch = (e:any) => {
        console.log(e);
        console.log(props.Account)
        fetchData('vapt/reports', 'GET', "?quarter=" + props.Account + "&phase=" + e).then((res: any) => {
            console.log(res)

        })
       // props.SwitchView('subdomains', e)
    }

    const token = localStorage.getItem("token");
    console.log(props)
    if (token === null || token === undefined || token.length < 0) {
        return <Redirect to={{
            pathname: '/login',
        }} />
    }
    console.log(domains)
    return (
        <>
            <MDBContainer fluid className="pb-5" style={{ padding: "2vw" }}>
                <MDBRow>
                    <MDBCol sm="11" md="11" lg="11" style={{ padding: "0px" }}>
                        <MDBBreadcrumb >
                            <MDBBtn className="back" onClick={() => props.SwitchView('home')} color="blue-grey" style={{ border: "2px solid blue-grey", borderRadius: "5px", marginLeft: "0px !important", fontSize: "8px !important" }}>
                                <MDBIcon icon="arrow-left" style={{ fontSize: "8px" }} /> Back</MDBBtn>
                            <MDBBreadcrumbItem>dashboard</MDBBreadcrumbItem>
                            <MDBBreadcrumbItem active>pentest</MDBBreadcrumbItem>
                        </MDBBreadcrumb>
                    </MDBCol>
                </MDBRow>
                <br />
                <MDBRow>
                    <MDBCol sm="11" md="11" lg="11" style={{ padding: "10px 25px"}}>
                        <MDBRow md="12" lg="12" style={{ display: "inline-flex" }}>
                            <p className='text-dark font-weight-bold' style={{ fontSize: "22px", paddingTop: "2px" }}>Penetration test</p>
                            <span className='text-dark' style={{ fontSize: "12px", marginLeft: "10px", paddingTop: "12px" }}>Last tested on 
                            {(domains.length > 0) ? domains[0].lastSynced : ""}</span>
                        </MDBRow>
                        {/* <br /> */}
                        {/* <MDBRow style={{ marginTop: "2vh" }}>
                            <MDBCol xs="4" sm="4" md="4" lg="4">
                                <div style={{ display: "inline-flex" }}>
                                    <MDBIcon style={{ backgroundColor: "#ff8c00", fontSize: "40px", color: "white" }}
                                        icon="check" />
                                    <p style={{ marginLeft: "20px" }}> <span style={{ fontSize: "25px", fontWeight: "bold" }}> {domains.length >0 ?  1 : 0}</span> <br /> In progress</p>
                                </div>
                            </MDBCol>
                            <MDBCol xs="4" sm="4" md="4" lg="4">
                                <div style={{ display: "inline-flex" }}>
                                    <MDBIcon style={{ backgroundColor: "#008450", fontSize: "40px", color: "white" }}
                                        icon="check-double" />
                                    <p style={{ marginLeft: "20px" }}> <span style={{ fontSize: "25px", fontWeight: "bold" }}> {domains.length-1}</span> <br /> completed</p>
                                </div>
                            </MDBCol>
                            <MDBCol xs="4" sm="4" md="4" lg="4">
                                <div style={{ display: "inline-flex" }}>
                                    <MDBIcon style={{ backgroundColor: "#dc3912", fontSize: "40px", color: "white" }}
                                        icon="exclamation-triangle" />
                                    <p style={{ marginLeft: "20px" }}> <span style={{ fontSize: "25px", fontWeight: "bold" }}> {domains.length >0 ?  0 : domains.length-1}</span> <br /> In submission</p>
                                </div>
                            </MDBCol>
                        </MDBRow> */}
                    </MDBCol>
                </MDBRow>
                {/* <br /> <br /> */}
                <MDBRow >
                    <MDBCol sm="11" md="11" lg="11" >
                        <div style={{ maxHeight: "70vh", overflowY: "auto", overflowX: "hidden" }}>
                            <MDBRow className="awsRow">

                                { (domains.length > 0) ?
                                    domains.map((dom: any) =>
                                        <>
                                            <MDBCol className="pentestList" sm="2" md="2" lg="2" onClick={() => SubDomainSwitch(dom.name) }>
                                                <div className="domainCard">
                                                    <p className='text-dark font-weight-bold' style={{ fontSize: "20px", marginBlockEnd: "0px" }}>{dom.name}
                                                    {/* <MDBIcon icon="file-download" style={{ fontSize: "25px", float: "right" }} /> */}
                                                    </p>
                                                    <p style={{ fontSize: "12px", color: "chocolate" }}>{dom.totalCount} issues</p>
                                                    <Chart width={200} series={[dom.criticalCount, dom.highCount, dom.mediumCount, dom.lowCount]} />
                                                </div>
                                            </MDBCol>
                                            <MDBCol sm="1" md="1" lg="1" style={{ width: "20px !important" }}></MDBCol>
                                       </>
                                    )
                                    :
                                    <div><h6 style={{ fontWeight: 800 }}>No pentests performed in this quarter.</h6></div>
                                }
                            </MDBRow>
                        </div>
                    </MDBCol>
                </MDBRow>
            </MDBContainer>
        </>
    )
}
