import React, { useRef, useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import { Redirect } from 'react-router';
import {
    MDBBtn,
    MDBCol,
    MDBContainer,
    MDBRow,
    MDBInput
} from "mdbreact";
import logo from '../../assets/images/logo-large.png'

export const LoginPage = (props: any) => {

    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [response, setResponse] = useState('');
    const [success, setSuccess] = useState('');
    const [validCred, setValidCred] = useState('');

    useEffect(() => {
        if (success === "true") {
            const tk = props.UserChange(response)
            console.log("success", success)
        } else if (success === "false") {
            const tk = props.UserChange(undefined)
        }
    }, [success]);

    useEffect(() => {
        if (validCred === "true") {
            setSuccess("true")
        }
    }, [validCred]);

    const Login = () => {
        console.log(email)
        console.log(password)
        const requestHeaders = {
            method: 'GET',
            headers: { 'Content-Type': 'application/json', 'mode': 'cors' },
            //  body: JSON.stringify({ "username": email, "password": password, "grant_type": "password_grant" })
        };

        fetch('http://srihari-ubuntuvm1.eastus.cloudapp.azure.com:9000/profile-service/api/profiles', requestHeaders).then(res => res.json()).then((data) => {
            setResponse(data)
            data.token.length > 10 ? setValidCred("true") : setValidCred("false")
        })
    }
    if (success === "true") {
        return <Redirect to={{
            pathname: '/candidate',
            state: { data: response }
        }} />
    }
    return (
        <MDBContainer fluid>
            <MDBRow style={{ height: "100vh" }}> 
                <MDBCol lg="4" style={{ backgroundColor: "black", opacity: "1"}}>
                {/* <img src={logo} className="" width="90%" style={{marginTop: "30vh"}}/> */}
                </MDBCol>
                <MDBCol sm="12" lg="8" >
                    <MDBContainer style={{ marginTop: "20vh", marginLeft: "8vh"  }}>
                        <MDBRow>
                        <p style={{ marginBottom: "10vh", fontWeight: 700 }} className="h1 text-center"><span style={{fontWeight: 600, fontSize: "45px" }}>O</span>nboard to Watcher</p>
                        </MDBRow>
                        <MDBRow>
                            <MDBCol md="5">
                                <form>
                                    <div className="grey-text">
                                        <MDBInput label="Type your username" icon="envelope" group type="email" validate error="wrong"
                                            value={email} onChange={e => setEmail((e.target as any).value)} success="right" />
                                        <MDBInput label="Type your password" icon="lock" group type="password" validate
                                            value={password} onChange={e => setPassword((e.target as any).value)} />
                                    </div>
                                    <div>
                                        <MDBBtn color="dark"
                                            onClick={() => Login()}>Login</MDBBtn>
                                    </div>
                                    <p style={{ marginTop: "5vh" }} className="mb-4">Unable to login ? <a href="/Signup" style={{ textDecoration: "underline"}}>Contact us</a></p>
                                </form>
                            </MDBCol>
                        </MDBRow>
                    </MDBContainer>
                </MDBCol>
            </MDBRow>
            </MDBContainer>
    );
}
