import { Chart } from "../common/VulnChart"
import { useHistory } from "react-router";
import "./style.css";
import { fetchData } from '../../services/apiConfig'
export const Pentest = (props: any) => {
    const [domains, setDomain] = useState([{
        "name": "Phase",
        "period": "quarter-3",
        "infoCount": 10,
        "lowCount": 10,
        "mediumCount": 10,
        "highCount": 10,
        "criticalCount": 10,
        "totalCount": 10,
        "lastSynced": "()"
      },
      {
        "name": "Phase",
        "period": "quarter-3",
        "infoCount": 10,
        "lowCount": 10,
        "mediumCount": 10,
        "highCount": 10,
        "criticalCount": 10,
        "totalCount": 10,
