import React, { useRef, useState, useEffect, useLayoutEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBListGroup,
    MDBListGroupItem,
    MDBView,
    MDBMask,
    MDBNotification
} from "mdbreact";
import { useHistory } from "react-router";
import "./style.css"
import Charts from "react-apexcharts";
import { Chart } from "../common/VulnChart"
import { CategoryChart } from "../common/VulnCatChart"
import { OrgChart } from "../common/OrgChart"
import { GradeChart } from "../common/GradeChart"
import { fetchData } from '../../services/apiConfig'
import { TimeSeries } from '../common/TimeSeries'
export const Home = (props: any) => {
