import React, { useState, useEffect } from "react";
import { Redirect } from 'react-router';
import { Header } from "../../common/Header";
import {
    MDBBtn,
    MDBFreeBird,
    MDBCard,
    MDBCardBody,
    MDBCardImage,
    MDBCardText,
    MDBCol,
    MDBContainer,
    MDBFormInline,
    MDBIcon,
    MDBInput,
    MDBRow,
    MDBTable,
    MDBTableBody,
    MDBTableHead,
    MDBEdgeHeader,
    MDBCardTitle,
    MDBProgress,
    MDBListGroup,
    MDBListGroupItem,
    MDBBreadcrumbItem,
    MDBBreadcrumb,
    MDBNotification,
    MDBLink
} from "mdbreact";
import { useHistory } from "react-router";
import { Chart } from "react-google-charts";
import "./style.css"
import { fetchData } from '../../services/apiConfig';
import scm1 from "../../assets/images/scm1.png"
export const ScmWatcher = (props: any) => {
    const token = localStorage.getItem("token");
    const [view, setView] = useState(false);
    const [webhook, setWebhook] = useState('');
    const [platform, setPlatform] = useState('');
    const [togglePlatform, setTogglePlatform] = useState('')
    const [whconnected, setWhconnected] = useState(false);
    useEffect(() => {
        fetchData('settings', 'GET').then((res: any) => {
            console.log(res)
            if(res.platform !== undefined && res.platform !== null)  {
                setView(true)
                setWhconnected(true)
                setWebhook(res.value)
                setPlatform(res.platform)
            }
        })
    }, [props]);
    const handleSwitchChange = (id: any) => {
        console.log(id)
        if(platform.length < 1){
            togglePlatform === id ?  setTogglePlatform('') : setTogglePlatform(id)
            console.log(togglePlatform.length)
            if(togglePlatform === id){
                setView(false)
            } else {
                setView(true)
            }
        }

    }
    const ConnecttoWebhook = () => {
        console.log("connect")
        togglePlatform.length> 0 ? setPlatform(togglePlatform) : setPlatform(platform)
        fetchData('/alerts', 'POST', { "platform": platform || togglePlatform, "webhook_url": webhook }).then((res: any) => {
            console.log(res.status)
            if(res.status === 204) {
                console.log("found")
                setWhconnected(true)
            } else {
                console.log("not found")
            }
        })
        setWhconnected(true)
    }
    const EditWebhook = () => {
        console.log("edit")
        setView(true)
        setWhconnected(false)
    }
    if (token === null || token === undefined || token.length < 0) {
        return <Redirect to={{
            pathname: '/login',
        }} />
    }

    return (
        <>
            <MDBContainer fluid className="pb-5" style={{ padding: "2vw" }}>
                <MDBRow >
                        <MDBBtn className="float-right" 
                            color="dark"
                            onClick={() => props.SwitchView('home')}
                            style={{ border: "2px solid blue-grey", borderRadius: "10px" }}>
                            <MDBIcon fas icon="angle-left" style={{ fontSize: "15px", marginLeft: "3px" }} />
                            &nbsp;&nbsp; Go back to dashboard&nbsp;&nbsp;
                        </MDBBtn>



                </MDBRow>

                <MDBRow style={{display: "flex", justifyContent: "center"}}>
                <h1 style={{fontWeight: 800}}>SCM Watcher</h1>
                <br /><br />
                {/* <div>
                    <img src={scm1} style={{ width: "500px", marginLeft: "20px" }} />
                </div> */}
                </MDBRow>
                <br />
                <MDBRow style={{display: "flex", justifyContent: "center"}}>
                {/* <h1 style={{fontWeight: 800}}>SCM Watcher</h1> */}
                <br />

                <div>
                    <img src={scm1} style={{ width: "50vw", marginLeft: "20px", height: "55vh", opacity: 0.2 }} />
                </div>

                </MDBRow>
                <div style={{display: "flex", justifyContent: "center", marginTop: "-35vh"}}>
                <MDBIcon icon="cog" spin size="6x" fixed />
                  <span className="sr-only">Loading...</span>
                </div>
            </MDBContainer>
        </>
    )
}
